function recogerTareas(){$.ajax({url:"cargar-tareas.php",type:"post",dataType:"html",success:function(a){$("#tareas").html(a)}})}recogerTareas();setInterval(recogerTareas,30000);$("#guardar").click(function(){var c=$("#texto").val();var a=$("#estado").val();var b=$("#usuario_id").val();$.ajax({url:"guardar-tareas.php",type:"post",data:{texto:c,estado:a,usuario_id:b},success:recogerTareas})});$(document).on("click",".borrar",function(){var a=$(this).data("id");$.ajax({url:"borrar-tareas.php",type:"post",data:{id:a},success:recogerTareas})});