function recogerTareas(){$.ajax({url:"cargar-tareas.php",type:"post",dataType:"html",success:function(a){$("#tareas").html(a)}})}$("#guardar").click(function(){var c=$("#texto").val();var a=$("#estado").val();var b=$("#usuario_id").val();$.ajax({url:"guardar-tareas.php",type:"post",data:{texto:c,estado:a,usuario_id:b},success:function(){recogerTareas();recogerTareasJson()}})});$(document).on("click",".borrar",function(){var a=$(this).data("id");$.ajax({url:"borrar-tareas.php",type:"post",data:{id:a},success:function(){recogerTareas();recogerTareasJson()}})});function recogerTareasJson(){$.ajax({url:"cargar-tareas-json.php",type:"post",dataType:"json",success:function(b){$("#tareas-json").html("");for(var a=0;a<b.length;a++){$("#tareas-json").append("<tr>");$("#tareas-json").append("<td>"+b[a].id+"</td>");$("#tareas-json").append("<td>"+b[a].texto+"</td>");$("#tareas-json").append("<td>"+b[a].estado+"</td>");$("#tareas-json").append("<td>"+b[a].usuario_id+"</td>");$("#tareas-json").append("<td><a href='#' class='borrar' data-id='"+b[a].id+"'>Borrar</a></td>");$("#tareas-json").append("<tr>")}}})}recogerTareas();setInterval(recogerTareas,30000);recogerTareasJson();setInterval(recogerTareasJson,30000);